mapper.update:
update todo_item
   set title = #{title}
     , content = #{content}
     , is_completed = #{completed}
     , start_dt = #{startDt}
     , update_dt = NOW()
 where seq = #{seq}

mapper.insert:
INSERT INTO todo_item (
      title
    , content
    , is_completed
    , start_dt
    , create_dt
)
VALUES (
      #{title}
    , #{content}
    , #{completed}
    , #{startDt}
    , #{createDt}
)
